mapper.insert:
INSERT INTO MEMBER (
	EMAIL, PW, BIRTHDAY, PHONE, NICKNAME
)
VALUES(
#{email},
#{pw},
#{birthday},
#{phone},
#{nickname}
)

mapper.update:
UPDATE MEMBER 
SET PW = #{pw}, 
PHONE = #{phone},
BIRTHDAY = #{birthday},
NICKNAME = #{nickname} 
WHERE EMAIL = #{email}

mapper.select:
SELECT EMAIL 
  FROM MEMBER 
 WHERE EMAIL=#{email jdbcType=VARCHAR}

SELECT PHONE 
  FROM MEMBER 
 WHERE  PHONE=#{phone}
